tinymce.PluginManager.add("addmediadocs", function (e, t) {
    function n() {
        var e;
        return e = $('#lst-medias-docs').html();
    }
    
   e.addButton("code", {
            type: "panelbutton",
            popoverAlign: "bc-tl",
            panel: {
                autohide: !0,
                html: n,
                onclick: function (t) {
                    var n = e.dom.getParent(t.target, "a");
                    n && (e.insertContent('<a href="' + n.getAttribute("data-mce-url") + '" target="_blank"><img src="../images/ico_pdf_small.png" />' + n.getAttribute("data-mce-file") + '</a>'), this.hide())
                    /*n && (e.insertContent('<div class="six columns" style="border:1px solid #000;"></div><div class="six columns" style="border:1px solid #000;"></div>'), this.hide())*/
                }
            },
            tooltip: "Insert media document"
        })
})